(193, '_transient_feed_d117b5738fbd35bd8c0391cda1f2b5d9', 'a:6:{s:5:\"child\";a:1:{s:0:\"\";a:1:{s:3:\"rss\";a:1:{i:0;a:6:{s:4:\"data\";s:3:\"\n\n\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:7:\"version\";s:3:\"2.0\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:1:{s:7:\"channel\";a:1:{i:0;a:6:{s:4:\"data\";s:61:\"\n	\n	\n	\n	\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:16:\"WordPress Planet\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:28:\"http://planet.wordpress.org/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"language\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"en\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:47:\"WordPress Planet - http://planet.wordpress.org/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"item\";a:50:{i:0;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:115:\"Do The Woo Community: The Power of Photography, Translation and Contributing with V Gautham Navada and Bigul Malayi\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:28:\"https://dothewoo.io/?p=93848\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:113:\"https://dothewoo.io/the-power-of-photography-translation-and-contributing-with-v-gautham-nevada-and-bigul-malayi/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:210:\"In this episode, guests V Gautham Navada, and Bigul Malayi discuss contributions to WordPress, highlighting the significance of photography and translation in enhancing community engagement and business growth.\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 03 Apr 2025 14:17:47 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"BobWP\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:1;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:94:\"Do The Woo Community: Why User Feedback and Internal Testing Drive Smarter Feature Development\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:43:\"https://dothewoo.io/?post_type=blog&p=93508\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:98:\"https://dothewoo.io/blog/why-user-feedback-and-internal-testing-drive-smarter-feature-development/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:206:\"User-centric development by involving support teams in feature planning and conducting beta tests. The approach leverages internal tools and data-driven insights to enhance product functionality and design.\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 03 Apr 2025 10:07:52 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"BobWP\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:2;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:77:\"WPTavern: 163  Birgit Pauli-Haack on the Magic of the WordPress Playground\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:48:\"https://wptavern.com/?post_type=podcast&p=193436\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:92:\"https://wptavern.com/podcast/163-birgit-pauli-haack-on-the-magic-of-the-wordpress-playground\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:52923:\"Transcript<div>\n<p>[00:00:00] <strong>Nathan Wrigley:</strong> Welcome to the Jukebox Podcast from WP Tavern. My name is Nathan Wrigley.</p>\n\n\n\n<p>Jukebox is a podcast which is dedicated to all things WordPress. The people, the events, the plugins, the blocks, the themes, and in this case what the WordPress Playground is, and how it&#8217;s transforming the scope of WordPress.</p>\n\n\n\n<p>If you&#8217;d like to subscribe to the podcast, you can do that by searching for WP Tavern in your podcast player of choice, or by going to wptavern.com/feed/podcast, and you can copy that URL into most podcast players.</p>\n\n\n\n<p>If you have a topic that you&#8217;d like us to feature on the podcast, I&#8217;m keen to hear from you and hopefully get you or your idea featured on the show. Head to wptavern.com/contact/jukebox, and use the form there.</p>\n\n\n\n<p>So on the podcast today, we have Birgit Pauli-Haack. Birgit is a longtime WordPress user, an influential voice in the WordPress community. She&#8217;s known for her role as the curator at the Gutenberg Times, and host of the Gutenberg Changelog podcast, and she brings her wealth of experience as a Core contributor to WordPress as well.</p>\n\n\n\n<p>She joins me today for an in-person conversation recorded at WordCamp Asia in the Philippines, and we are discussing Playground, a remarkable development that&#8217;s set to redefine the WordPress development landscape.</p>\n\n\n\n<p>Playground allows users to launch a fully functional WordPress instance directly in their browser, without the necessity of a server, database, or PHP, playground breaks down barriers, offering developers, product owners, educators, and everyone in between a new way to interact with WordPress.</p>\n\n\n\n<p>We explore how this technology not only simplifies the testing and development process, but also sets the stage for more interactive and immediate web experiences.</p>\n\n\n\n<p>We explore the concept of Blueprints within Playground, tailored configurations that enables a bespoke user experience by preloading plugins, themes, and content. This feature helps developers to present their work in a controlled environment, offering users an insightful hands-on approach that can significantly enhance understanding and engagement, and it&#8217;s all available with just one click. It really does eliminate the traditional hurdles associated with installing WordPress.</p>\n\n\n\n<p>If you&#8217;re curious about how the WordPress Playground is set to usher in a new era of friction free web development, this episode is for you.</p>\n\n\n\n<p>If you&#8217;re interested in finding out more, you can find all of the links in the show notes by heading to wptavern.com/podcast, where you&#8217;ll find all the other episodes as well.</p>\n\n\n\n<p>And so without further delay, I bring you Birgit Pauli-Haack.</p>\n\n\n\n<p>I am joined on the podcast by Birgit Pauli-Haack. Hello Birgit.</p>\n\n\n\n<p>[00:03:28] <strong>Birgit Pauli-Haack:</strong> Oh, hey Nathan.</p>\n\n\n\n<p>[00:03:29] <strong>Nathan Wrigley:</strong> We&#8217;re actually looking at each other, not through a screen.</p>\n\n\n\n<p>[00:03:32] <strong>Birgit Pauli-Haack:</strong> Yes. It&#8217;s a total different feeling.</p>\n\n\n\n<p>[00:03:34] <strong>Nathan Wrigley:</strong> Yeah. Birgit And I chat a lot on various other channels, and it&#8217;s a pleasure having you right in front of me. That&#8217;s lovely.</p>\n\n\n\n<p>[00:03:39] <strong>Birgit Pauli-Haack:</strong> Yeah, same here. I&#8217;m always glad we meet at a WordCamp.</p>\n\n\n\n<p>[00:03:42] <strong>Nathan Wrigley:</strong> Yeah, thank you. So that&#8217;s the introduction then because here we are, we&#8217;re at WordCamp Asia, in the Philippines. It&#8217;s the first day of the conference in general. We had the Contributor Day yesterday, and we&#8217;ve got another day tomorrow.</p>\n\n\n\n<p>And we&#8217;re going to have a chat with Birgit who is going to be talking to us today about Playground, because you&#8217;ve got a slot at the event all about creating a demo in Playground. And we&#8217;ll get onto that in a minute. But first of all, for those people who don&#8217;t know who you are, just a few moments for your potted bio. Tell us about yourself.</p>\n\n\n\n<p>[00:04:09] <strong>Birgit Pauli-Haack:</strong> So I&#8217;m the curator at the Gutenberg Times and I&#8217;m the host on the Gutenberg Changelog podcast. And I also am a Core contributor to WordPress, and I work for Automattic. I live in Munich and I&#8217;m married, 37 years.</p>\n\n\n\n<p>[00:04:22] <strong>Nathan Wrigley:</strong> There we go. That is a very potted bio. Thank you, I appreciate that.</p>\n\n\n\n<p>So here we are, we&#8217;re going to talk about Playground. And I figured the best place to start is answering the question, what is Playground? And just before we hit record, it was pretty obvious that both you and I are very excited about this. And so I want to encourage people to really pay attention because this genuinely, for me is one of the most exciting developments, not just now, but ever, in WordPress. It truly is a transformational technology. But for those who don&#8217;t know what it is, just tell us what Playground is.</p>\n\n\n\n<p>[00:04:54] <strong>Birgit Pauli-Haack:</strong> I&#8217;m totally with you there on the magic, yeah. And it&#8217;s not just for WordPress, it&#8217;s for web development. So WordPress Playground is a WordPress instance in your browser. Yeah, you go there, put in playground.wordpress.net. You get a full WordPress instance in your browser, and you can add plugins, you can themes, you can content. Test it out. Whatever you do with that and want to learn with Playgrounds, you don&#8217;t need a server, you don&#8217;t need a database, you don&#8217;t need PHP installed or something like that. So it&#8217;s just there.</p>\n\n\n\n<p>And for someone who has been in the web development for many, many years, it&#8217;s like magic. Because before you&#8217;re always kind of, oh, where do I host things? What&#8217;s with the database? What&#8217;s with the server? And it&#8217;s all gone. Yeah, so it&#8217;s really cool.</p>\n\n\n\n<p>[00:05:43] <strong>Nathan Wrigley:</strong> I think probably it&#8217;s best on this particular podcast to avoid the technicalities, but I would point the listener to a podcast that I did on the WP Tavern with Adam Zielinski several months ago now, where Adam came on and tried, in an audio form, it&#8217;s very hard to do, but explained in an audio form exactly what the underpinnings are.</p>\n\n\n\n<p>And the only words I can use to describe it are, it&#8217;s voodoo. It is literal magic. Just two or three years ago, if you&#8217;d have said that Playground was possible, I honestly would&#8217;ve thought that you were talking nonsense. It could not happen. That will never happen.</p>\n\n\n\n<p>[00:06:18] <strong>Birgit Pauli-Haack:</strong> Snake oil.</p>\n\n\n\n<p>[00:06:18] <strong>Nathan Wrigley:</strong> Yeah, exactly. And yet Adam managed to pull it off. And so just to re-explain what Birgit just said, it&#8217;s all in the browser. When you go to playground.wordpress.net, there is no server. Just say it again, there&#8217;s no server. There&#8217;s no PHP that you need to install on your local machine. It all happens inside the browser. Close the browser down, it goes away. We&#8217;ll come to that. Maybe that&#8217;s changed.</p>\n\n\n\n<p>But the idea is it&#8217;s happening in the browser, and so you can have any combination of website that you like immediately inside of Playground, and it really is remarkable.</p>\n\n\n\n<p>I liken to the moment that the iPhone got the App Store. The iPhone was a very useful thing to have. You know, it did phone calls and it looked beautiful, and you could upload music to the phone with a cable. And then along came the App Store, and suddenly a thousand, a million, different developers could get their hands on it and tell you, here&#8217;s a different way you can use the iPhone. And here&#8217;s another way, and here&#8217;s another thing that you can do. And it feels a bit like Playground is WordPress&#8217; moment like that. You know, it just suddenly prizes the lid open, and makes developers able to show you what they&#8217;ve got in a heartbeat.</p>\n\n\n\n<p>[00:07:25] <strong>Birgit Pauli-Haack:</strong> Yeah. And that&#8217;s pretty much, that&#8217;s a very good analogy because we also have a Blueprints gallery that could be something like an app store where you can learn how you can assemble it. So the core technologies is not not, I don&#8217;t know any of the technology that&#8217;s underlying. It&#8217;s based on Web Assembly. And that has been around for about 10 years, trying to get a lot of different programming languages talk to each other in the browser.</p>\n\n\n\n<p>And then it&#8217;s based, not on MySQL, but on SQLite database. And then Service Workers and worker Threads API, that are browser APIs. For storage, for instance, yeah, or for sending commands to other different applications. But that&#8217;s all I know, yeah. I have never worked with Web Assembly, yeah. And MySQL, I know that, just really amazing.</p>\n\n\n\n<p>So you can use that. Many people use it to spin up a fully functional WordPress and demo that. So you can use it in educational settings. You don&#8217;t have to download a whole lot of stuff. You don&#8217;t have to, as a teacher, you don&#8217;t have to set up, talk to your IT department to set up a server for all the students. You can just point them to the Playground and then give them instructions on how to work with that.</p>\n\n\n\n<p>It&#8217;s a sandbox environment. It could be, yeah, if you want to. You can upload your content and then see what else can you change with it without messing with your live site. You can integrate it with your development. There is a WP now, VS Code extension where you can, so when you&#8217;re working on your plugin and you click on the button, it loads up a local Playground for you with the plugin that you&#8217;re working on already installed, and that&#8217;s really cool.</p>\n\n\n\n<p>Same with the theme. The training team has been working on interactive demos in terms of having code examples on one side, and then you make changes to the code and you see it in the right hand side. How it changes the website. So that&#8217;s really cool.</p>\n\n\n\n<p>[00:09:20] <strong>Nathan Wrigley:</strong> I think one of the things that you said there, you&#8217;ve got an understanding of some of the underlying technologies, but you were stressing that, basically you don&#8217;t need to understand them. Having a knowledge of them is fun, you know, it&#8217;s interesting. But a bit like I don&#8217;t have the faintest idea how to build an iPhone app, but I can still use an iPhone. And I can still benefit from this application, the maps, navigation app. I don&#8217;t need to understand how that&#8217;s built, but I can use it, it works.</p>\n\n\n\n<p>And really that&#8217;s, I think the purpose. The developers over there, thank you so much, but most people are never probably going to want to get into the weeds of that. They just want to click the button and see what happens.</p>\n\n\n\n<p>And just to be clear on this, if you&#8217;ve never done that, I, at my home, have a fairly good internet connection, so I don&#8217;t know if I&#8217;m in a sort of slightly privileged position, but when I click the button at playground.wordpress.net, I&#8217;m imagining it&#8217;s somewhere in the order of three to four seconds before that website is ready to go. Basically it&#8217;s the length of time it takes me to blink and grab the mouse again. It&#8217;s in a heartbeat. So there&#8217;s literally no friction.</p>\n\n\n\n<p>But if you go to playground.wordpress.net and click the button, what you&#8217;re going to get there is a vanilla version of WordPress, which is fine. Then you can do whatever you like with that, put plugins in, what have you. But wouldn&#8217;t it be interesting, wouldn&#8217;t it be great if somebody came up with, oh, I don&#8217;t know, let&#8217;s call them Blueprints or something like that, where you could pre-build something that then somebody else could use.</p>\n\n\n\n<p>So this is the App Store, isn&#8217;t it? You know, somebody&#8217;s built the maps navigation app. Somebody&#8217;s built the note taking app. Somebody&#8217;s built the whatever. This feels like what the Blueprints are. But I want to make sure that you are describing it and not me because I am not sure that I&#8217;ve encapsulated it perfectly.</p>\n\n\n\n<p>[00:11:00] <strong>Birgit Pauli-Haack:</strong> No, you did. But in opposite to the App Store, you actually can look at other people&#8217;s Blueprints and steal them. Blueprints are written in JSON has nothing to do with Jason. It&#8217;s JSON. It&#8217;s a data format for JavaScript. And there is a schema for it, so when you put it into your code editor, it gives you signals, yeah, that you formatted right.</p>\n\n\n\n<p>And then you have two different ways of configuring your Playground instance. One is to do settings. So you could do which PHP you want to use? Which WordPress version do you want to use? Also, do you want to have network enabled? And most of the time you want it enabled because you want to import and install themes or something like that. Those are the settings.</p>\n\n\n\n<p>And then you have steps. And those steps are also just formulated in JSON format. For instance, you can log in. Automatically log in the person in the Playground. Or you can say, I have a landing page that should land, so when somebody uses that blueprint, when Playground is ready to completely load it, you should land in the block editor, for instance. And you should have that particular block plugin already active on that post, so you can really play with blocks. Nick Diego with his plugin Block Visibility has done a great way for a live preview of his block from the repository.</p>\n\n\n\n<p>Another way is to, so install a plugin, add content to it. Use WP-CLI to instantly load up new versions, add new pictures, or use an export from another website, an XML file from another website and load it into the Playground instance.</p>\n\n\n\n<p>But sometimes you have, you said you get the vanilla if you just do that, if you just do playground.wordpress.net, you get the vanilla WordPress. But it&#8217;s one post, Hello World, and it&#8217;s one sample page. But you don&#8217;t see how content kind of interacts with whatever feature you want to demo. So you need some content there, yeah. And the Blueprints Gallery has actually some nice examples on how to configure that.</p>\n\n\n\n<p>[00:13:08] <strong>Nathan Wrigley:</strong> Let&#8217;s come back to the gallery in a minute. Just to recap what you just said. So there&#8217;s a bunch of settings, probably more for developers. You know, you might want to test something in a particular PHP environment or what have you, so you can select those. And then you can do these steps where you can essentially design, if somebody was to use that Playground and somebody was to click on your link, they would wait the 2, 3, 4 seconds, whatever, and then, depending on the steps that you&#8217;d set up, they would arrive where you chose them to be.</p>\n\n\n\n<p>So for example, you might pre-install the latest, greatest plugin that you want to share with the world. And you want people in a post for that. And you want them inside the block editor. And you can make it so that upon clicking the button, the first thing they get is, we&#8217;re inside your plugin, we&#8217;re about to use it. So the profundity of that is pretty amazing. You can really tailor the experience.</p>\n\n\n\n<p>So rather than going from being like Playground, which sounds like children, you&#8217;re messing about, larking about a little bit. It also becomes like serious ground a little bit, you know? Serious developers can use this to circumvent, I don&#8217;t know, support tickets, the capacity to demonstrate to users who&#8217;ve never seen your product before, your plugin, your theme, or whatever it may be.</p>\n\n\n\n<p>You can point them to a link. They can click the link. You as the developer configure everything within an inch of its life, so they get exactly where you want them to be. And in that way you can use it as a sales mechanism, as a support mechanism.</p>\n\n\n\n<p>[00:14:29] <strong>Birgit Pauli-Haack:</strong> And sometimes it&#8217;s really hard to tell people what your plugin does unless you show it them in the video. But then they still don&#8217;t get their hands on it. And with that feature, with the Playground combined with the Blueprints, you can actually make them feel the thing. How it works with them, and what ideas they get when they play around with it, and have better questions, educated questions for you, for the product, yeah.</p>\n\n\n\n<p>[00:14:51] <strong>Nathan Wrigley:</strong> So a Blueprint then is a version of Playground in which somebody has pre-configured things. Is that basically what it is? You know, let&#8217;s say that I have got this fabulous new plugin and I want you to experience it. I don&#8217;t necessarily want you to land on a particular page, but I just want the plugin to be available to you and you can do things.</p>\n\n\n\n<p>If I install my plugin, use Playground to do that, I can then share a link. And because I&#8217;ve tinkered with it, it becomes a Blueprint because it&#8217;s not the playground.wordpress.net version, it&#8217;s my doctored version, adapted version.</p>\n\n\n\n<p>[00:15:26] <strong>Birgit Pauli-Haack:</strong> Well, it also goes to playground.wordpress.net, but it has a query parameter, to be a little technical term, that says, use the blueprint at this URL. So a plugin developer for the repository, at the repository there are live preview buttons now. And the plugin developer can put in a separate directory Blueprints on the WordPress site, put all the assets, all the image that they want to load, and the configuration file, which is written in this JSON file, and put it there, and then make that live. And every time someone clicks on the preview button, they go to playground.wordpress.net with the Blueprint kind of loaded, the configuration files.</p>\n\n\n\n<p>[00:16:09] <strong>Nathan Wrigley:</strong> So it&#8217;s all happening through playground.wordpress.net. But then there&#8217;s JSON configuration file, which gets sort of sideloaded, if you like, through the URL. That tells it, okay, add this and then end up here and what have you. The important part is that JSON, that&#8217;s what makes it the Blueprint. It&#8217;s going to playground.wordpress.net, but the JSON file means that it does something else.</p>\n\n\n\n<p>And you said the word gallery, which tells me that there&#8217;s a whole host of these things. Pre-configured, pre-built, put into a box if you like. And we can go to that gallery and explore. What kind of stuff is in there?</p>\n\n\n\n<p>[00:16:38] <strong>Birgit Pauli-Haack:</strong> So, what kind of stuff is there? So there&#8217;s one, how do I put an admin notice on top of the dashboard? How do I add a dashboard widget and load it up with my Playground? So most of the time, when you want to log into a WordPress site, you get the dashboard. And if there&#8217;s a widget, you can actually guide people to go some other places. You can say, okay, I have a plugin that needs 50 posts, for whatever reason. So there is a Blueprint there and how to use WP-CLI to create 12 or 50 posts automatically, that are then loaded into the post content.</p>\n\n\n\n<p>So there&#8217;s also a Blueprint for a specific WooCommerce extension. So it loads WooCommerce, it loads the extension, it loads some products, and then you land for a shipping page where you can say, okay, this shipping plugin, what does it do for me? And you see it working with products on a Playground site. So that is really remarkable. It takes a little longer when you have content to load.</p>\n\n\n\n<p>[00:17:38] <strong>Nathan Wrigley:</strong> Goes up to like 10 seconds.</p>\n\n\n\n<p>[00:17:40] <strong>Birgit Pauli-Haack:</strong> So you go and get your coffee and come back.</p>\n\n\n\n<p>[00:17:42] <strong>Nathan Wrigley:</strong> But it&#8217;s still profound.</p>\n\n\n\n<p>[00:17:43] <strong>Birgit Pauli-Haack:</strong> Yeah, remarkable. Yeah, you don&#8217;t have to do anything, kind of just wait a bit.</p>\n\n\n\n<p>What else is in there? Oh, there is a demo of 2025. So when you load 2025 theme automatically and go to your website and see it, you get the post, the blog site, where all the posts are in one one big site with the full content. And not a whole lot of people have that kind of blog. And in the demo, you actually go to the magazine front page, and then see all the patterns that are in there. You can see all the templates in that Playground demo.</p>\n\n\n\n<p>That&#8217;s interesting for plugin developers that have experimental themes or experimental settings on the settings page that you can actually preload them as well. There&#8217;s an example in there for the Gutenberg experiments. They&#8217;re on the check marks on a setting site. And you can take that and replicate that for your own plugins site, how to do that, with the areas.</p>\n\n\n\n<p>Because you can do site options. So the site options is not only site title and tag descriptions, also, oh, make my block editor have the top toolbar instead of all the other things or the distraction free model, yeah. So these kind of features, you can also preload there and have examples from the Blueprints Gallery.</p>\n\n\n\n<p>[00:18:57] <strong>Nathan Wrigley:</strong> I think we&#8217;re just at the beginning really, aren&#8217;t we? Of of this journey. And basically, the underlying technology is now provisioned. It&#8217;s there. And we&#8217;re at point where, okay, people, developers, explore. And we&#8217;re really just at the beginning of that. And the gallery is probably a good place to go.</p>\n\n\n\n<p>But if you wanted to put one of these JSON files together, do you know, is there some credible documentation out there that would help people to get started, learn the ropes?</p>\n\n\n\n<p>[00:19:25] <strong>Birgit Pauli-Haack:</strong> Yeah, there&#8217;s definitely, there&#8217;s documentation of all the steps that are there, yeah, like how to run PHP, how to have additional PHP extensions installed and all that. So when you open the Playground, there are three, and you&#8217;re not going to the full page, so you have three panes. On the left hand side you have some menus, and one of them is the documentation link. So that&#8217;s good.</p>\n\n\n\n<p>And another link is there, it&#8217;s the Blueprint Gallery. So in the middle of the section of your Playground, you see all the list of all the gallery content. And then when you click on the preview or the view site, the Playground loads that for you, and then there&#8217;s another menu item where is says, view Blueprint. And that gives you a Blueprint editor.</p>\n\n\n\n<p>So you see the Blueprint loaded in, but then when you want to edit from the documentation, okay, what happens when I put that in? And you click the run button, and it reloads that Playground with your changes. So it&#8217;s really, very hands on, and you still don&#8217;t have to create a server or a local environment or something like that.</p>\n\n\n\n<p>[00:20:31] <strong>Nathan Wrigley:</strong> Yeah, there&#8217;s this really virtuous cycle of, okay, so you&#8217;ve used something from the gallery, but you&#8217;re curious about how it works. Look, here&#8217;s how it works. Here&#8217;s the buttons to click to go and explore. Oh, and whilst you&#8217;re at it, if you want to edit anything, here&#8217;s the option to edit it. And when you click save, it&#8217;ll restart that whole thing and you&#8217;ll get the new version.</p>\n\n\n\n<p>So all of the sort of helpful tooling is now built into it. Because when I talked to Adam, none of that existed. I mean, the version selection for PHP didn&#8217;t exist. The ability to land people on particular destinations when they first load up the playground, none of that existed. It was literally the technology of getting it working.</p>\n\n\n\n<p>So now built into it is this knowledge base, if you like. Not really a knowledge base, but more, you want to know how this one works? We&#8217;ll show you. And it&#8217;s that beautiful, well, the purpose of WordPress, democratising publishing. In this case, it&#8217;s democratising the nuts and the bolts, and the bits and pieces of publishing.</p>\n\n\n\n<p>Yeah, so that&#8217;s really nice. And that&#8217;s all built inside. So just follow the prompts in the UI, and you can adapt what you want, and what have you. But also there are some 101 articles out there, perhaps on Learn or something like that where can see in text format how do all.</p>\n\n\n\n<p>[00:21:40] <strong>Birgit Pauli-Haack:</strong> Yeah, the developer blog has, on developer.wordpress.org/news has three articles about Playground. One is about the underlying technology from the Web Assembly people. That was really good for those who want to explore that even further.</p>\n\n\n\n<p>And then there is one on what use cases you can do with a little bit of an example. And then also, so we are right now always talking about playground.wordpress.net. But you mentioned something that someone could put this on their website, and you can.</p>\n\n\n\n<p>Playground can be self-hosted. It does not have to go through the wordpress.net site. But how to do this is in the documentation. It has a seperate section there. So if you say, okay, I don&#8217;t have my plugin in the repo, but I want to use it through my own website, then you can actually put it there, and it&#8217;ll have your own branding around it. So it&#8217;s even get further than just the WordPress part.</p>\n\n\n\n<p>[00:22:35] <strong>Nathan Wrigley:</strong> So that&#8217;s a really important distinction to make. So in the cases that we&#8217;ve been talking about so far, if you want to go to playground.wordpress.net and you use your own JSON file, it will be able to suck in anything from the WordPress repo. And that&#8217;s the sort of, the WordPress way, if you like. I&#8217;m doing air quotes.</p>\n\n\n\n<p>[00:22:51] <strong>Birgit Pauli-Haack:</strong> Also from GitHub.</p>\n\n\n\n<p>[00:22:52] <strong>Nathan Wrigley:</strong> Oh, thank you. Yeah, that&#8217;s an important distinction. I&#8217;d forgotten that. Also from GitHub, but you know, it&#8217;s everything that&#8217;s open source out there, free to download already.</p>\n\n\n\n<p>But a big part of the WordPress community, one of the things that makes it popular, is the ability to sell commercial plugins. And so that was another question that I had. Is possible to do it?</p>\n\n\n\n<p>And so, yes, but you need to take the technology that builds WordPress at playground.wordpress.net, you put that onto your own server, and you can do whatever you like with that. So you can put your premium products in there on a, I don&#8217;t know, two day free trial sort of basis, and show people how that all works.</p>\n\n\n\n<p>So Playground suddenly becomes more interesting outside of the free to play area as well. And you can imagine that being a really, really useful tool. Because we&#8217;ve always been able to play fairly straightforwardly with free things on the repo, but suddenly the moment where you&#8217;ve got to pay $100 for a thing, the capacity to see that really is the bit which opens the wallet.</p>\n\n\n\n<p>Okay, it&#8217;s $100, maybe I&#8217;ll buy it, maybe I won&#8217;t. It&#8217;d be nice to see it. Okay, they&#8217;ve got a 14 day trial, but I&#8217;ve still got to pay for it. This opens up the capacity to, look, there it really is. Play with it for two days or whatever it may be. That&#8217;s fascinating.</p>\n\n\n\n<p>[00:24:05] <strong>Birgit Pauli-Haack:</strong> Absolutely, yeah. And if you want to test that plugin, yeah, you still would need a local server or a hosting server to load it on. And you have that 14 day trial. And now you can really test it right now.</p>\n\n\n\n<p>[00:24:16] <strong>Nathan Wrigley:</strong> Right. And that&#8217;s the other big thing. Because if you buy a commercial plugin, you then have to spin up a site somehow. You have to download the plugin, upload the plugin, get the plugin configured. This gets rid of all of that, because you don&#8217;t need to download and upload anything, and it can be pre-configured.</p>\n\n\n\n<p>So the author of the plugin can say, okay, if you want to use my LMS plugin for this kind of thing, here&#8217;s playground version with everything just right. And if you want to do it for this kind of thing, I don&#8217;t know, you&#8217;re an elementary school teacher who might use my LMS plugin in this way, or you&#8217;re a university lecturer, who might use it in this way. Let&#8217;s build it a perfect version for you.</p>\n\n\n\n<p>And you can imagine that a million times over for all the commercial plugins out there. You know, form plugins. Okay, this is the contact form that we&#8217;ve pre-built. This is the, I don&#8217;t know, the form which integrates with WooCommerce or whatever. So the developers can do all of this. And that really makes it super useful to them.</p>\n\n\n\n<p>[00:25:11] <strong>Birgit Pauli-Haack:</strong> Yes, absolutely, yeah. What&#8217;s coming down the pipeline for Playground. One is that you can also use it with private GitHub repos. Which right now is not possible, but it&#8217;s in the works. And there was a problem with the proxy, that you get some cross site downloading errors because some servers are not set up to have images downloaded from a machine. They have created a proxy server now, where that is kind of circumvented that you can also from non WordPress sites download stuff, like images and content, or PHP plugins.</p>\n\n\n\n<p>What also comes is, so SQL, MySQL, for some plugins Playground does not work yet, because they use very specific MySQL query, the union query, for instance. Select union and other commands like that. The SQLite doesn&#8217;t have those yet. And they are however working on it to replicate these kind of behavior of a database also with Playground. So to make it even more compatible with all the plugins that are out there.</p>\n\n\n\n<p>I think they did a test of 10,000 plugins that are in the repo, and test every month kind of how many plugins don&#8217;t work with it yet. And they got it down from, I think 7% to 5%. So it&#8217;s always kind of progressing very well towards zero.</p>\n\n\n\n<p>[00:26:33] <strong>Nathan Wrigley:</strong> Yeah, there&#8217;s a lot of things going on in the background that the likes of you and I probably, you know, because we&#8217;re curious about it, we&#8217;ll probably know about, but maybe the average listener who&#8217;s not wedded to this subject maybe doesn&#8217;t. But that&#8217;s really interesting.</p>\n\n\n\n<p>So the intention is to get it so that more or less anything works in more or less any scenario. And really nicely putting it out there so that you can do things which aren&#8217;t bound to GPL, WordPressy kind of things, if you know what I mean. So, you know, you can use your commercial product over here, and you can use your GitHub repo over here. That&#8217;s really nice.</p>\n\n\n\n<p>My understanding is that when Adam began it, he was immediately repurposed. So Adam Zielinski, he was an, was, still is, I think, an Automattician. And I think that it was immediately understood, this is profound. Let&#8217;s get Adam on this full time. You know, it&#8217;s no longer a hobby project. But I also think that he&#8217;s got other people from Automattic involved. There&#8217;s like a little team around it now, pushing the development of that. Is that still the case? Is this a team which is growing, or stagnating at, well not stagnating, maintaining at a certain number?</p>\n\n\n\n<p>[00:27:33] <strong>Birgit Pauli-Haack:</strong> Well, it&#8217;s growing in scope. So they&#8217;re also working, and that was a focus starting in last fall, that they&#8217;re working on using Playground for the Data Liberation Project. And that&#8217;s what Adam was doing also full-time now in the last few months. That he looks, okay, what kind of parser do we need to do really good data liberation from other systems, or from WordPress?</p>\n\n\n\n<p>Yeah, because the import and export in WordPress only gets you so far, yeah. And there are some quirks in there, and they want to really have a perfect data liberation through Playground. They have a browser extension. It&#8217;s all beta right now. It&#8217;s not functioning yet. But it&#8217;s really coming along quite nicely.</p>\n\n\n\n<p>[00:28:20] <strong>Nathan Wrigley:</strong> So Data Liberation then is this very laudable project of being able to bring into WordPress, I guess data liberation on some levels is the whole point of open source really, isn&#8217;t it? Is that you can grab your data and just pick it up and take it somewhere else.</p>\n\n\n\n<p>[00:28:34] <strong>Birgit Pauli-Haack:</strong> Open content.</p>\n\n\n\n<p>[00:28:35] <strong>Nathan Wrigley:</strong> Right, yeah. It&#8217;s your content. This platform is no longer being used, or you&#8217;ve fallen out with it. You know, you no longer love it in the way that you did. You want to now move it here. And you&#8217;ll be able to, let&#8217;s say, go Joomla into WordPress, Drupal into WordPress, or as you said, WordPress into WordPress.</p>\n\n\n\n<p>Which suddenly kind of opens up the whole idea of migrating websites, which a real mess frankly. It&#8217;s a really difficult thing to do. And I often think that people are bound to products and services that they&#8217;re purchasing on a monthly basis because the migration process is so difficult. And they don&#8217;t want to be caught up in all of that because things can go wrong. You know, it might not work perfectly and there&#8217;s all the just carrying it out.</p>\n\n\n\n<p>But if you can essentially do migrations, and Playground is the sort of go between. It&#8217;s the bit which talks from, I don&#8217;t know, one hosting company to another. So it goes from hosting company A to Playground. Playground then serves it up to hosting company B, which is where you want to end up. And all of that happens through Playground. That&#8217;s remarkable. And you can do the inspecting in the middle bit, the middleware, Playground if you like. Check it&#8217;s all working before you deploy it. That&#8217;s amazingly powerful.</p>\n\n\n\n<p>[00:29:41] <strong>Birgit Pauli-Haack:</strong> Yeah. And that&#8217;s actually the vision of Playground&#8217;s part of Data Liberation. They also have a browser extension to kind of identify a non WordPress site, the various pieces like the pages, the posts, the news, the events, kind of the custom post types. And then kind of teach Playground what it all is. But that&#8217;s kind of, it&#8217;s very technical on one side, but it&#8217;s also, you need to have a total different concept about content management systems to actually make that. So that&#8217;s not really for a normal consumer.</p>\n\n\n\n<p>[00:30:13] <strong>Nathan Wrigley:</strong> Yeah, because if you&#8217;re coming from Drupal and you&#8217;ve got like 1,000 different modules in there, you know, think plugins in the WordPress space. Then it&#8217;s going to be difficult to one-to-one map that over to WordPress. But the endeavor is to do a half decent job and in the middle you can step in and say, okay, this might need modifying, that might need modifying. And then you can go back to your Drupal install, change things a little bit, try again because it takes no time to do it. That is really a key, interesting part. You do kind of wonder actually if hosting companies in the future will just offer Playground in as part of their bundle, you know, their onboarding migrating bundle.</p>\n\n\n\n<p>[00:30:47] <strong>Birgit Pauli-Haack:</strong> Yeah. A lot of hosting companies have their own plugins for that. So I know that Pressable and SpinupWP, they all have their, or wordpress.com has their own plugin that they then connect with. I think it&#8217;s BlogVault most of the time. Pantheon, same, yeah. Where you can migrate in. But that part in the middle, that kind of always takes a long time.</p>\n\n\n\n<p>And you are bound to the hosting company to actually offer that, yeah. And that&#8217;s not a cheap plugin. But if you go from one small hosting to one, another small hosting, you don&#8217;t have that luxury.</p>\n\n\n\n<p>[00:31:20] <strong>Nathan Wrigley:</strong> Yeah, and if you&#8217;re crossing platforms as well, say Joomla into WordPress and what have you. That&#8217;s also really different.</p>\n\n\n\n<p>[00:31:25] <strong>Birgit Pauli-Haack:</strong> Yeah. There are a few agencies who have built for their customer things, but it&#8217;s not open source and it&#8217;s, well, it&#8217;s open source, but it&#8217;s not meant for a huge amount of public to kind of use it.</p>\n\n\n\n<p>[00:31:36] <strong>Nathan Wrigley:</strong> Yeah, I&#8217;d imagine that it&#8217;s fairly proprietary technology, isn&#8217;t it? It&#8217;s probably locked down because it&#8217;s the secret source of getting the Drupal installs into WordPress on their platform.</p>\n\n\n\n<p>One of the things which Adam spoke about when we talked, I don&#8217;t know where we&#8217;re at with this, but I raised the question of the destructibility of it. So essentially when I spoke to Adam, when you launched Playground, you fiddle with it, played with it, the moment you click close on the browser tab everything went away. That&#8217;s how it was designed. But he said that at some point in the near future, and maybe that moment has already been passed.</p>\n\n\n\n<p>[00:32:09] <strong>Birgit Pauli-Haack:</strong> It&#8217;s here.</p>\n\n\n\n<p>[00:32:09] <strong>Nathan Wrigley:</strong> Yeah, so now we&#8217;ve got a more permanent version. Tell us about that. Are there any constraints on that? Like, can I close the browser tab? Can I shut my computer down, for example? I mean, will it last forever? Could I even use it as a, I don&#8217;t know, as a temporary website in, let&#8217;s say I work in a school and I want an intranet for my staff or something, could for those kind of things?</p>\n\n\n\n<p>[00:32:29] <strong>Birgit Pauli-Haack:</strong> Well, it cannot be, it doesn&#8217;t have a domain or something like that. So that wouldn&#8217;t work. But yes, you can save. You have two options to save the site that you&#8217;re working on, so you can come back tomorrow. One is in the browser. So it uses the local storage of the browser and really downloads the whole WordPress stuff there. And then you open up the browser again, you get the site again. You cannot load it from another computer because it&#8217;s a different browser.</p>\n\n\n\n<p>And the second option is to load it in your local file system. So you can, it downloads the whole thing, gives you a directory and that&#8217;s your website, and you can load it then back into Playground a day later, or a week later, or two months later, because it&#8217;s still on your computer.</p>\n\n\n\n<p>You can also have multiple sites now in one Playground instance. So you can say, okay, save this site, and then now I use another blueprint, load it again and it&#8217;s another temporary site. And you load it, you save it again, then you have a second website there.</p>\n\n\n\n<p>[00:33:29] <strong>Nathan Wrigley:</strong> A curious version of version control or something like that. You&#8217;ve added this plugin in, I&#8217;m going to save a new version marking that this plugin got added. Let&#8217;s see how that works. And then if it doesn&#8217;t work, we can roll back to the, just delete that one and go back to the previous one. Oh gosh. So essentially permanent. Locally permanent maybe is the better way to describe it.</p>\n\n\n\n<p>[00:33:50] <strong>Birgit Pauli-Haack:</strong> And you need to think about the saving part. If you do a second site and you close it, a browser without the saving part, it&#8217;s going to go away. Yeah, it&#8217;s still ephemeral there. Which is also a good thing sometimes.</p>\n\n\n\n<p>[00:34:02] <strong>Nathan Wrigley:</strong> But obviously as you said, you know, the point of hosting in the end is that, you know, it connects to a domain name, it goes through the DNS process and you you can see it online. No.</p>\n\n\n\n<p>[00:34:10] <strong>Birgit Pauli-Haack:</strong> No, not yet.</p>\n\n\n\n<p>[00:34:11] <strong>Nathan Wrigley:</strong> This is not. Oh, not yet. I wonder.</p>\n\n\n\n<p>[00:34:12] <strong>Birgit Pauli-Haack:</strong> No, no, I don&#8217;t think that&#8217;s ever going to be. But what can be, soon hopefully is kind of pushing it to a hosting company. And that, I think it needs to be just finalised which hosting is going to be there. And the Playground team learns a lot from wordpress.com, because the new development, local development system that wordpress.com has, Studio, is based on Playground. They develop some of the features also for, that wordpress.com can use them in their Studio. And what was the bug fixes? Come to Playground.</p>\n\n\n\n<p>[00:34:46] <strong>Nathan Wrigley:</strong> That makes real sense though, for hosting companies to be clamoring all over this, to build a Playground import functionality. Because then developers all over the world, you know, maybe if in teams it might be a little bit more difficult, but you know, a solo developer, certainly at the moment, you&#8217;ve been working on something. You&#8217;ve got this perfect version of the site, you&#8217;ve got all the plugins that you want, you&#8217;ve set it up, it&#8217;s working on my machine. Now I go over to my hosting company of choice, click the import Playground button and there it is. Why wouldn&#8217;t the hosting companies offer that frankly, it just seems too straightforward.</p>\n\n\n\n<p>[00:35:17] <strong>Birgit Pauli-Haack:</strong> Syncing up with the live site or there&#8217;s also a GitHub deployment there. It opens so many ideas, yeah. And when you ask Adam, well, if I think about this, and can you do that? He said, sure.</p>\n\n\n\n<p>[00:35:28] <strong>Nathan Wrigley:</strong> Give a few weeks. I&#8217;ll add it to list of 1,000 things that people have already suggested.</p>\n\n\n\n<p>[00:35:32] <strong>Birgit Pauli-Haack:</strong> Yeah, we need to develop that. Yeah, the ideas are there, the prototypes are there, the proof of concept is already done. Just a matter of resources now, yeah. I can for instance see one thing is, if you have a documentation and you need people to contribute to documentation, you load the documentation in Playground, you make the changes, and then you push it to GitHub as a pull request. And then somebody can review it, load it in their own Playground and approve it so the documentation could be updated.</p>\n\n\n\n<p>Something like that is already in use. That scenario, that&#8217;s in prototype. It&#8217;s not there yet, but we know that it can work, because some theme developers have that process. They&#8217;re not developers per se, that they go into the files. They load the theme into Playground, use the Create Block Theme plugin. Make the changes to the theme. Save it and create the block theme, so it&#8217;s in files. Then push it to GitHub as a pull request for this theme, and then have all the changes there. So that&#8217;s how a lot of designers work with their developers on the themes. They don&#8217;t have to touch any code, but it&#8217;s still all saved in code.</p>\n\n\n\n<p>[00:36:48] <strong>Nathan Wrigley:</strong> It&#8217;s just such an interesting beginning of everything. It does feel like we are at a moment where there&#8217;s just so many different roads that could be taken, and lots of people coming up with lots of different ideas.</p>\n\n\n\n<p>Just quickly circling back to the Studio thing that you mentioned. So Studio is a local development environment. You&#8217;re going to be downloading this as a software bundle for your Mac or your Windows machine or what have you. You&#8217;re saying that&#8217;s a wrapper for Playground, is it?</p>\n\n\n\n<p>[00:37:13] <strong>Birgit Pauli-Haack:</strong> Exactly.</p>\n\n\n\n<p>[00:37:13] <strong>Nathan Wrigley:</strong> But that&#8217;s immutably stored. That&#8217;s not dependent on.</p>\n\n\n\n<p>[00:37:17] <strong>Birgit Pauli-Haack:</strong> No, it&#8217;s on your machine, yeah.</p>\n\n\n\n<p>[00:37:19] <strong>Nathan Wrigley:</strong> Right. So it&#8217;s going for the files on the machine approach as opposed to being stored in the browser. So if you download and make use of Studio, you can close that machine down, come back to it whenever you like, it&#8217;s there until you decide to delete it.</p>\n\n\n\n<p>[00:37:32] <strong>Birgit Pauli-Haack:</strong> Like any other local environment that you can, yeah.</p>\n\n\n\n<p>[00:37:35] <strong>Nathan Wrigley:</strong> Yeah, okay. And that&#8217;s available free you to download for anybody.</p>\n\n\n\n<p>[00:37:38] <strong>Birgit Pauli-Haack:</strong> Free, open source.</p>\n\n\n\n<p>[00:37:39] <strong>Nathan Wrigley:</strong> Okay. Is there anything else you wanted to cover off, apart from the fact that we&#8217;ve both got ridiculously excited about this. Was there anything curious, interesting, quirky, novel that you&#8217;ve seen out there that we haven&#8217;t yet touched?</p>\n\n\n\n<p>[00:37:50] <strong>Birgit Pauli-Haack:</strong> No, not yet. But I&#8217;m starting now to kind of dream about it. And sooner or later I come up with something, yeah.</p>\n\n\n\n<p>What I would want and what I want to pursue is that I can have a Playground instance for writers. And I know writers who are not very keen on using the Block Editor, because it gets in the way. But the Block Editor has these settings where you can do distraction free, where you can do, put the toolbar on top, yeah, and hide it as long as I write, and just let me have when I&#8217;m not writing kind of thing, and log in and not have to go to the menu.</p>\n\n\n\n<p>Right now, if I&#8217;m a blogger, I have to log into WordPress, and then I need to look at post, new post. This would give you, start writing, and don&#8217;t have to worry about the rest of it. And then click a button and then your WordPress site is updated with it. That&#8217;s kind of what I&#8217;m working on. I don&#8217;t know if really helpful, but.</p>\n\n\n\n<p>[00:38:44] <strong>Nathan Wrigley:</strong> No, that&#8217;s really great. I mean, one of the things that I always thought was curious about it would be the idea in education, for educators literally standing in front of pupils, children who, you know, depending on what the kind of curriculum they&#8217;ve got. It might be we&#8217;re doing about poetry. We want everybody to upload and modify a poem, or comment on a poem or something like that.</p>\n\n\n\n<p>And here&#8217;s the link. You know, we&#8217;re in an environment where everybody&#8217;s, we&#8217;re in the computer lab, everybody&#8217;s got a computer. Just click on this link, scan the QR code, whatever it may be. Give us your modifications, what have you. And I know that&#8217;s a sort strange example, but it&#8217;s the fact that instantly, very, very inexperienced users are in the same exact interface as all the other experienced users. And the level of difficulty was clicking a link. You just needed to click a link.</p>\n\n\n\n<p>And the educator didn&#8217;t need a great deal of technology to set it up. The pupils needed zero technology to access it. And so it&#8217;s that one to many thing, where lots and lots of people can access the same thing in a heartbeat. And I&#8217;m imagining that the tooling to create the Playground installs, and to create the Blueprints is going to make it more and more easy in the future. So possibly not the perfect example, but I do like the example of one to many.</p>\n\n\n\n<p>[00:39:56] <strong>Birgit Pauli-Haack:</strong> Yeah. What I like about it is that it&#8217;s not about WordPress. It&#8217;s about poetry. It&#8217;s about writing. It&#8217;s about, well, even image uploading and editing, yeah. You could certainly do that. Technology gets out of the way. And for the last 25 years, that&#8217;s always been in the way, yeah, and now it&#8217;s out of the way.</p>\n\n\n\n<p>[00:40:14] <strong>Nathan Wrigley:</strong> Well, because the internet is basically a reading experience. I mean, I know we&#8217;ve got forms, but really all you&#8217;re doing is submitting a form so that somebody can read that. But you go to any website and largely websites, you know, if you&#8217;re going to some sort of SaaS app, that&#8217;s a different thing, it&#8217;s configured probably to be more interactive. But broadly speaking, you&#8217;re going to consume information.</p>\n\n\n\n<p>But in this, you click a link and you&#8217;re reading information, but then you can do things with it. Oh, I think it would be better if there was an image there in that poem. Or, I don&#8217;t know, it&#8217;s an explanation of some principle of physics or something, and a diagram would be really useful at this point, and I don&#8217;t like the way they describe that, that could go in bold. And you are interacting with the internet. And it&#8217;s totally free, and it will be easy to deploy, and it&#8217;ll take seconds to load. And all of a sudden the internet became more interactive. And it&#8217;s just the beginning. It&#8217;s very exciting.</p>\n\n\n\n<p>[00:41:05] <strong>Birgit Pauli-Haack:</strong> Yeah, it is.</p>\n\n\n\n<p>[00:41:06] <strong>Nathan Wrigley:</strong> Birgit Pauli-Haack, thank you very much for talking to me today.</p>\n\n\n\n<p>[00:41:09] <strong>Birgit Pauli-Haack:</strong> Thank you for leading me down the road of all the ideas here.</p>\n\n\n\n<p>[00:41:13] <strong>Nathan Wrigley:</strong> Thank you for explaining it.</p>\n</div>\n\n\n\n<p>On the podcast today we have <a href=\"https://profiles.wordpress.org/bph/\">Birgit Pauli-Haack</a>.</p>\n\n\n\n<p>Birgit is a long time WordPress user, an influential voice in the WordPress community. Shes known for her role as the curator at the Gutenberg Times and host of the Gutenberg Changelog podcast. And brings her wealth of experience as a Core contributor to WordPress as well.</p>\n\n\n\n<p>She joins me today for an in-person conversation, recorded at WordCamp Asia in the Philippines, and were discussing Playground, a remarkable development that&#8217;s set to redefine the WordPress development landscape.</p>\n\n\n\n<p>Playground allows users to launch a fully functional WordPress instance directly in their browser. Without the necessity of a server, database, or PHP, Playground breaks down barriers, offering developers, product owners, educators and everyone in between a new way to interact with WordPress.</p>\n\n\n\n<p>We explore how this technology not only simplifies the testing and development process, but also sets the stage for more interactive and immediate web experiences.</p>\n\n\n\n<p>We explore the concept of Blueprints within Playground, tailored configurations that enable a bespoke user experience by preloading plugins, themes, and content. This feature helps developers to present their work in a controlled environment, offering users an insightful hands-on approach that can significantly enhance understanding and engagement, and its all available with just one click. It really does eliminate the traditional hurdles associated with installing WordPress.</p>\n\n\n\n<p>If youre curious about how the WordPress Playground is set to usher in a new era of friction-free web development, this episode is for you.</p>\n\n\n\n<h2 class=\"wp-block-heading\">Useful links</h2>\n\n\n\n<p><a href=\"https://gutenbergtimes.com/\">Gutenberg Times</a></p>\n\n\n\n<p><a href=\"https://gutenbergtimes.com/feed/podcast/gutenberg-changelog/\">Gutenberg Changelog podcast</a></p>\n\n\n\n<p>Podcast with <a href=\"https://wptavern.com/podcast/122-adam-zielinski-on-how-playground-is-transforming-wordpress-website-creation\">Adam Zielinski on How Playground Is Transforming WordPress Website Creation</a></p>\n\n\n\n<p><a href=\"https://playground.wordpress.net/\">WordPress Playground</a></p>\n\n\n\n<p><a href=\"https://wordpress.org/plugins/block-visibility/\">Block Visibility plugin</a> by Nick Diego</p>\n\n\n\n<p><a href=\"https://make.wordpress.org/meta/2024/05/15/the-blueprint-gallery-share-your-wordpress-creations-with-playground/\">Playground Blueprints Gallery</a></p>\n\n\n\n<p><a href=\"https://developer.wordpress.org/news/\">WordPress Developer Blog > News</a></p>\n\n\n\n<p><a href=\"https://wordpress.org/data-liberation/\">Data Liberation Project</a></p>\n\n\n\n<p><a href=\"https://spinupwp.com/\">SpinupWP</a></p>\n\n\n\n<p><a href=\"https://blogvault.net/\">BlogVault</a></p>\n\n\n\n<p><a href=\"https://pantheon.io/\">Pantheon</a></p>\n\n\n\n<p><a href=\"https://developer.wordpress.com/studio/\">WordPress Studio</a></p>\n\n\n\n<p><a href=\"https://wordpress.org/plugins/create-block-theme/\">Create Block Theme plugin</a></p>\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 02 Apr 2025 14:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Nathan Wrigley\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:3;a:6:{s:4:\"data\";s:13:\"\n	\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5